sys_variable_value.value:
(function execute(inputs, outputs) {


  sleep(0);
  function sleep(ms) {
    var dt = new Date();
    var sleeptime = dt.getTime();
    while(new Date().getTime() < sleeptime + ms) {}
outputs.newdatetime = sleeptime;
outputs.currentdate = sleeptime; 
  };



})(inputs, outputs);

