sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...


var str = inputs.u_body;
//var str = '{"id":651754,"type":"job","url":"/api/v2/jobs/651754/"}';
var obj = JSON.parse(str);

outputs.u_id = obj.id;
outputs.u_status = obj.status;
outputs.u_failed = obj.failed;
outputs.u_started = obj.started;
outputs.u_finished = obj.finished;




})(inputs, outputs);

